      "aliases": ["My wife"],
      "claims": [
        {
          "text": "The authors wife experienced severe neurological and behavioral symptoms including confusion, hallucinations, insomnia, and emotional instability.",
          "source": "health_book_draft:chunk_1"
        },
        {
          "text": "She was diagnosed with hyperparathyroidism caused by an overactive parathyroid gland producing excess hormone.",
          "source": "health_book_draft:chunk_1"
        },
        {
          "text": "She underwent surgical removal of the affected parathyroid gland and began recovery afterward.",
          "source": "health_book_draft:chunk_1"
        }
      ]
